import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';


class App extends Component {
  state = {
    persons:[
      {id :"sdcnso",name:'Max', age:'30'},
      {id :"djvnds",name:'Manu',age:'29'},
      {id :"dvjnsdjn",name:'Kanchan',age:'22'}
    ],
    showName : false
  } 

  switchNameHandler = (newName) => {
    this.setState({persons: [
      {name: newName, age:'30'},
      {name:'Manu',age:'29'},
      {name:'Kanchan',age:'23'}     
    ]
    }) 
  }

  nameChangeHandeller = (event) => {
    this.setState({persons: [
      {name: "Rozen", age:'30'},
      {name: event.target.value ,age:'29'},
      {name:'Kanchan',age:'23'}     
    ]}) 
  }

  toggleNameRenderer = () => {
    const currentName = this.state.showName;
    this.setState({showName: !currentName});
  }

  deleteNameHandeller = (personIndex) =>{
    const persons = this.state.persons.slice();
    persons.splice(personIndex, 1);
    this.setState({persons: persons});
  }

  render() {
    const style = {
      backgroundColor : 'white',
      font:'inherit', 
      border: '1x solid blue',
      padding: '8px',
      cursor :'pointer'
    };

    let person = null;
    if(this.state.showName)
    {
      person = (
          <div>
            {this.state.persons.map( (person,index) => {
              return <Person
              click = {this.deleteNameHandeller.bind(this,index)}
              name = {person.name}
              age = {person.age}
              key = {person.key}/>
            })} 

          </div>
        );
    }

    return (
      <div className="App">
        <h1>This is React Application</h1>
        <p>This is working</p>
        <button
          style = {style}
          onClick = {() => this.toggleNameRenderer() }>Switch name</button>
          {person}
      </div>
    );
  }
}

export default App;
