import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons:[
      {name:'Max', age:'30'},
      {name:'Manu',age:'29'},
      {name:'Kanchan',age:'22'}
    ]
  } 

  switchNameHandler = (newName) => {
    this.setState({persons: [
      {name: newName, age:'30'},
      {name:'Manu',age:'29'},
      {name:'Kanchan',age:'23'}     
    ]}) 
  }





  render() {
    return (
      <div className="App">
        <h1>This is React Application</h1>
        <p>This is working</p>
        <button onClick = {this.switchNameHandler(bind,"random")}>Switch name</button>
        <Person 
          name= {this.state.persons[0].name} 
          age = {this.state.persons[0].age}/>
        <Person 
          name= {this.state.persons[1].name} 
          age = {this.state.persons[1].age}
          click = {this.switchNameHandler(bind,"random_random")}>My hobby is racing
        </Person>
        <Person 
          name= {this.state.persons[2].name} 
          age = {this.state.persons[2].age}/>

      </div>
    );
  }
}

export default App;
