import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';


class App extends Component {
  state = {
    persons:[
      {name:'Max', age:'30'},
      {name:'Manu',age:'29'},
      {name:'Kanchan',age:'22'}
    ],
    showName : false
  } 

  switchNameHandler = (newName) => {
    this.setState({persons: [
      {name: newName, age:'30'},
      {name:'Manu',age:'29'},
      {name:'Kanchan',age:'23'}     
    ]
    }) 
  }

  nameChangeHandeller = (event) => {
    this.setState({persons: [
      {name: "Rozen", age:'30'},
      {name: event.target.value ,age:'29'},
      {name:'Kanchan',age:'23'}     
    ]}) 
  }

  toggleNameRenderer = () => {
    const currentName = this.state.showName;
    this.setState({showName: !currentName});
  }


  render() {
    const style = {
      backgroundColor : 'white',
      font:'inherit', 
      border: '1x solid blue',
      padding: '8px',
      cursor :'pointer'
    };

    let Person = null;
    if( this.state.showName)
    {
      Person :
        {
          <div>
            <Person 
              name= {this.state.persons[0].name} 
              age = {this.state.persons[0].age}/>
            <Person 
              name= {this.state.persons[1].name} 
              age = {this.state.persons[1].age}
              click = {this.switchNameHandler.bind(this,"random_random")}
              changed = {this.nameChangeHandeller}>My hobby is racing
            </Person>
            <Person 
              name= {this.state.persons[2].name} 
              age = {this.state.persons[2].age}/>
          </div>
        };
    }

    return (
      <div className="App">
        <h1>This is React Application</h1>
        <p>This is working</p>
        <button
          style = {style}
          onClick = {() => this.toggleNameRenderer() }>Switch name</button>
      </div>
    );
  }
}

export default App;
